import { Table, Container, Title, LoadingOverlay, Button, Flex, Text, Center } from '@mantine/core';
import { doc, deleteDoc } from 'firebase/firestore';
import { db } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';
import { Empty, Image, Pagination } from 'antd';
import { notifications } from '@mantine/notifications';
import { useState } from 'react';
import PropTypes from 'prop-types'

const Submissions = ({ guests, loading, error }) => {
    const navigate = useNavigate();

    const handleView = (id) => {
        navigate(`/submissions/${id}`);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this submission?")) {
            await deleteDoc(doc(db, 'guests', id));
            notifications.show({
                title: 'Success',
                message: 'Submission deleted successfully.',
                color: 'green',
            })
        }
    };

    const [activePage, setPage] = useState(1);

    const guestPerPage = 12;

    const onChange = (page) => {
        setPage(page);
    };

    const guestWithPagination = guests.slice((activePage - 1) * guestPerPage, activePage * guestPerPage);

    if (loading) return <LoadingOverlay visible />;
    if (error) return <div>Error: {error.message}</div>;

    return (
        <Container mt={40}>
            <Title order={2}>Guest Submissions</Title>
            {
                guests.length > 0 ? (
                    <Table highlightOnHover horizontalSpacing="md" verticalSpacing="md" >
                        <Table.Thead>
                            <Table.Tr>
                                <Table.Th></Table.Th>
                                <Table.Th>Image</Table.Th>
                                <Table.Th>Name</Table.Th>
                                <Table.Th>Actions</Table.Th>
                            </Table.Tr>
                        </Table.Thead>
                        <Table.Tbody>
                            {guestWithPagination.map((guest, index) => (
                                <Table.Tr key={guest.id}>
                                    <Table.Td>
                                        <Text style={
                                            {
                                                fontWeight: 'bold',
                                                backgroundColor: '#f5f5f5',
                                                padding: '5px 0px',
                                                borderRadius: '60px',
                                                textAlign: 'center'
                                            }
                                        }>
                                            {guestPerPage * (activePage - 1) + index + 1}
                                        </Text>
                                    </Table.Td>
                                    <Table.Td>
                                        <Image
                                            width={80}
                                            height={80}
                                            src={guest.image ? guest.image : "error"}
                                            fallback="data:image/png;base64,"
                                        />
                                    </Table.Td>
                                    <Table.Td>{guest.name}</Table.Td>
                                    <Table.Td>
                                        <Flex justify={'flex-start'} align={'center'} gap={'xs'}>
                                            <Button onClick={() => handleView(guest.id)}>View</Button>
                                            <Button color="red" onClick={() => handleDelete(guest.id)}>Delete</Button>
                                        </Flex>
                                    </Table.Td>
                                </Table.Tr>
                            ))}
                        </Table.Tbody>
                        <Table.Caption>
                        <Pagination current={activePage} pageSize={guestPerPage} onChange={onChange} total={guests.length} />

                        </Table.Caption>
                    </Table>
                )
                    : (
                        <Center mt={50}>
                            <Empty description="No submissions yet!" />
                        </Center>
                    )
            }
        </Container>
    );
};

Submissions.propTypes = {
    guests: PropTypes.array,
    loading: PropTypes.bool,
    error: PropTypes.any
}

export default Submissions;
